public class TestString {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int first = 1 ;
		int second = 2 ;
		int third = 3 ;
		String fourth = "c";
		System.out.println("Now fourth is: " + fourth) ;
		
		fourth = third + fourth;
		System.out.println("Now fourth is: " + fourth) ;
		
		fourth = second + fourth;
		System.out.println("Now fourth is: " + fourth) ;
		
		fourth = first + fourth;
		System.out.println("Now fourth is: " + fourth) ;

}
}